mapper.update:
UPDATE tw_attendance_employee SET
emp_id = #{empId}, attendance_id = #{attendanceId}, created_by = #{createdBy}, updated_by = #{updatedBy}, updated_date = now(),
WHERE link_id = #{linkId}

mapper.select:

select * from (
	select l.*, a.attendance_name attendanceName, e.emp_name empName, e.emp_no empNo, e.pinyin pinyin, e.store_id storeId, e.dpt_id dptId from 
	(select link_id linkId, emp_id empId, attendance_id attendanceId, created_by createdBy, created_date createdDate,
		updated_by updatedBy, updated_date updatedDate from tw_attendance_employee 

			 attendance_id = #{param.attendanceId}

	) l
	left OUTER join tw_attendance a on l.attendanceId=a.attendance_id
	left outer join tb_employee e on l.empId=e.emp_id
) t 

	`status`=0
	 and storeId = #{param.storeId}

		AND (
			empName like concat('%', #{param.namePYJob}, '%') 
			or pinyin like concat('%', #{param.namePYJob}, '%')
			or empNo like concat('%', #{param.namePYJob}, '%')
		) 

	AND dptId = #{param.dptId}


select * from (
	select e.*, l.link_id linkId from (
		select emp_id empId, emp_name empName, emp_no empNo, pinyin pinyin, store_id storeId, dpt_Id dptId 
		from tb_employee 

			 store_id = #{param.storeId}

				AND (
					emp_name like concat('%', #{param.namePYJob}, '%') 
					or pinyin like concat('%', #{param.namePYJob}, '%')
					or emp_no like concat('%', #{param.namePYJob}, '%')
				) 

			AND dpt_id = #{param.dptId} 

	) e LEFT OUTER JOIN tw_attendance_employee l on e.empId = l.emp_id
) t where linkId is null


select * from (
	select s.store_name storeName, e.*, a.link_id linkId, a.attendance_id attendanceId, d.attendance_name attendanceName, a.created_by createdBy, 
	a.created_date createdDate, a.updated_By updatedBy, a.updated_Date updatedDate from (
		select store_id storeId, emp_id empId, emp_name empName, emp_no empNo, pinyin pinyin, dpt_Id dptId from tb_employee 

				`status`=0
				 and store_id = #{param.storeId}

					AND (
						emp_name like concat('%', #{param.namePYJob}, '%') 
						or pinyin like concat('%', #{param.namePYJob}, '%')
						or emp_no like concat('%', #{param.namePYJob}, '%')
					) 

				AND dpt_id = #{param.dptId} 

	) e LEFT OUTER JOIN tb_store s on e.storeId=s.store_id
	LEFT OUTER JOIN tw_attendance_employee a on e.empId=a.emp_id
	LEFT OUTER JOIN tw_attendance d on a.attendance_id=d.attendance_id
) t

	 attendanceId = #{param.attendanceId}
